(()=>{if(new URLSearchParams(location.search).get("address"))return;const e=document.querySelector("[data-address-badge]"),t=document.querySelector("[data-address-text]"),a=document.querySelector("[data-address-clear]");if(!e||!t||!a)return;let d=[];try{d=JSON.parse(localStorage.getItem("savedAddresses:v2")||"[]")}catch{}const s=d.find(e=>e.isDefault);s&&(e.classList.remove("hidden"),t.textContent=s.label?`${s.label}: ${s.address}`:s.address,a.addEventListener("click",()=>{e.classList.add("hidden"),history.replaceState({},"",location.pathname)}))})();